dust.f.glsl:
uniform vec3 color;
uniform float time;

varying float vOffset;

void main() {
	gl_FragColor = vec4( color.r, color.g, color.b, clamp(pow(sin(vOffset + time / 2000.), 8.), 0., 0.95));
}

dust.v.glsl:
attribute float offset;

uniform float time;

varying float vOffset;

void main() {
	vOffset = offset;

	vec4 mvPosition = modelViewMatrix * vec4( position, 1.0 );
	gl_PointSize = 1800.0 / length( mvPosition.xyz );
	gl_PointSize = clamp(gl_PointSize, 0., 3.5);
	gl_Position = projectionMatrix * mvPosition;
	gl_Position.y = gl_Position.y + sin(vOffset * 100. + time / 10000.) * 50.;
}
